A = [1 5; 0.4 0]disp('Find the eigenvalues')eig(A)disp('RREF to solve for eigenvectors for first eigenvalue')[rref(A - (eig(A)(1))*eye(2)) zeros(2,1)]disp('RREF to solve for eigenvectors for second eigenvalue')[rref(A - (eig(A)(2))*eye(2)) zeros(2,1)]disp('Find eigenvalues from the characteristic polynomial')roots(poly(A))disp('Eigenvalues and eigenvectors directly')[Vecs Vals] = eig(A)disp('Convert from normalized to confirm answers above')disp('First eigenvector')Vecs(:,1)disp('Dividing by norm')Vecs(2,1)Vecs(:,1) / Vecs(2,1) disp('Second eigenvector')Vecs(:,2)disp('Dividing by norm')Vecs(2,2)Vecs(:,2) / Vecs(2,2)